window.onload=function(){var toggler=document.getElementsByClassName("caret");var i;for(i=0;i<toggler.length;i++){toggler[i].addEventListener("click",function(){this.parentElement.querySelector(".nested").classList.toggle("active");this.classList.toggle("caret-down")})}var handler=document.querySelector(".navigation-bar");var wrapper=handler.closest(".page-container");var navigation=wrapper.querySelector(".tree-navigation");var isHandlerDragging=false;document.addEventListener("mousedown",function(e){if(e.target===handler){isHandlerDragging=true}});document.addEventListener("mousemove",function(e){if(!isHandlerDragging){return false}var containerOffsetLeft=wrapper.offsetLeft;var pointerRelativeXpos=e.clientX-containerOffsetLeft;var navigationMinWidth=300;navigation.style.width=Math.max(navigationMinWidth,pointerRelativeXpos-8)+"px";navigation.style.flexGrow=0});document.addEventListener("mouseup",function(e){isHandlerDragging=false})};